// Historical Trends Component
const HistoricalTrends = ({ data }) => {
  if (!data || data.length === 0) return null;

  // Process data for the chart
  const chartData = data.map(item => ({
    year: item.year,
    deaths: item.total_deaths,
    affected: item.affected_population,
  }));

  return (
    <Card sx={{ height: '100%', p: 2 }}>
      <CardContent>
        <Typography variant="h6" sx={{ mb: 2 }}>Historical Trends</Typography>
        <LineChart width={350} height={300} data={chartData} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="year" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="deaths" stroke="#ef233c" name="Deaths" />
          <Line type="monotone" dataKey="affected" stroke="#2b2d42" name="Affected" />
        </LineChart>
      </CardContent>
    </Card>
  );
};

// Map Component
const DisasterMap = ({ country, geoJsonData }) => {
  const [mapCenter, setMapCenter] = useState([0, 0]);
  const [zoom, setZoom] = useState(4);

  useEffect(() => {
    if (geoJsonData) {
      try {
        const bounds = L.geoJSON(geoJsonData).getBounds();
        setMapCenter([
          (bounds.getNorth() + bounds.getSouth()) / 2,
          (bounds.getEast() + bounds.getWest()) / 2
        ]);
      } catch (error) {
        console.error('Error processing GeoJSON:', error);
        setMapCenter([0, 0]);
      }
    }
  }, [geoJsonData]);

  return (
    <Card sx={{ height: '100%', p: 2 }}>
      <CardContent>
        <Typography variant="h6" sx={{ mb: 2 }}>Impact Map</Typography>
        <div style={{ height: '400px', width: '100%' }}>
          <MapContainer
            center={mapCenter}
            zoom={zoom}
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; OpenStreetMap contributors'
            />
            {geoJsonData && (
              <GeoJSON
                data={geoJsonData}
                style={{
                  fillColor: '#ef233c',
                  weight: 2,
                  opacity: 1,
                  color: 'white',
                  fillOpacity: 0.7
                }}
              />
            )}
          </MapContainer>
        </div>
      </CardContent>
    </Card>
  );
};

