import React, { useState, useEffect } from "react";
import {
  Container,
  Typography,
  Button,
  TextField,
  Card,
  CardContent,
  CardMedia,
  AppBar,
  Toolbar,
  Link,
} from "@mui/material";
import { Grid } from '@mui/material'
import "./App.css";
import Autocomplete from "@mui/material/Autocomplete";
import axios from "axios";
import Typewriter from "typewriter-effect";
import { MapContainer, TileLayer, GeoJSON } from "react-leaflet"; // For rendering Leaflet maps
import "leaflet/dist/leaflet.css";

// Disaster Cards Component
const DisasterCards = () => {
  const disastersInfo = [ 
    { title: "Drought", description: "Prolonged period of insufficient rainfall." }, 
    { title: "Earthquake", description: "Sudden shaking of the ground." }, 
    { title: "Epidemic", description: "Widespread occurrence of an infectious disease in a community at a particular time." }, 
    { title: "Extreme Temperature", description: "Unusual and severe weather conditions, such as heatwaves or cold spells." }, 
    { title: "Flood", description: "Overflow of water submerging land." }, { title: "Landslide", description: "Earth sliding down a slope." }, 
    { title: "Storm", description: "A violent disturbance of the atmosphere with strong winds and usually rain, thunder, lightning, or snow." }, 
    { title: "Wildfire", description: "Uncontrolled fires in forests or grasslands." }
  ];

  return (
    <Grid container spacing={3}>
      {disastersInfo.map((disaster, index) => (
        <Grid item xs={12} sm={6} md={3} key={index}>
          <Card
            style={{
              height: "100%",
              backgroundColor: "#edf2f4",
              display: "flex",
              flexDirection: "column",
              borderColor: "#2b2d42",
              borderRadius: "40",             
            }}
          >

            <CardMedia
              component="img"
              // height="140"
              image={`/disasterImages/${disaster.title.toLowerCase()}.png`}
              alt={disaster.title}
              style={{ objectFit: "fit" }}
            />
            <CardContent style={{ flexGrow: 1 }}>
              <Typography
                variant="h6"
                style={{
                  fontFamily: "system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif",
                  color: "#ef233c",
                  fontWeight: "bold",
                  textAlign: "center",
                }}
              >
                {disaster.title}
              </Typography>
              <Typography
                variant="body2"
                style={{ color: "#2b2d42", textAlign: "center" }}
              >
                {disaster.description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

// Country Map Component
const CountryMap = ({ countryCode }) => {
  const [geoJsonData, setGeoJsonData] = useState(null);

  useEffect(() => {
    if (countryCode) {
      // Fetch GeoJSON data for the selected country
      axios
        .get(`https://raw.githubusercontent.com/datasets/geo-boundaries-world-110m/master/countries/${countryCode}.geojson`)
        .then((response) => {
          setGeoJsonData(response.data);
        })
        .catch((error) => {
          console.error("Failed to fetch GeoJSON data:", error);
        });
    }
  }, [countryCode]);

  return (
    <div style={{ height: "500px", width: "100%" }}>
      {geoJsonData ? (
        <MapContainer center={[0, 0]} zoom={2} style={{ height: "100%", width: "100%" }}>
          {/* Base map tiles */}
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />

          {/* Render GeoJSON data for the selected country */}
          <GeoJSON data={geoJsonData} />
        </MapContainer>
      ) : (
        <p>Loading map...</p>
      )}
    </div>
  );
};

const App = () => {
  const [country, setCountry] = useState(null);
  const [year, setYear] = useState(null);
  const [disasterType, setDisasterType] = useState(null);
  const [searchResults, setSearchResults] = useState(null);
