import React, { useState } from 'react';
import { 
  Tabs, 
  Tab, 
  Box, 
  Typography, 
  CircularProgress 
} from '@mui/material';

const AIAssistantTabs = ({ disaster }) => {
  const [tabValue, setTabValue] = useState(0);
  const [loading, setLoading] = useState(false);
  const [aiContent, setAIContent] = useState(null);

  const handleTabChange = async (event, newValue) => {
    setTabValue(newValue);
    setLoading(true);

    // Simulate AI content generation
    try {
      // In a real implementation, you'd call an actual AI service here
      const mockAIResponse = await simulateAIGeneration(newValue, disaster);
      setAIContent(mockAIResponse);
    } catch (error) {
      console.error('AI Content Generation Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const simulateAIGeneration = (tabIndex, disasterData) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        const contents = [
          `Summary of ${disasterData.disaster_type} in ${disasterData.country} (${disasterData.year})`,
          `Public safety information for the ${disasterData.disaster_type} event`,
          `Citizen engagement strategies for ${disasterData.disaster_type}`,
          `Educational content about ${disasterData.disaster_type}`
        ];
        resolve(contents[tabIndex]);
      }, 1500);
    });
  };

  return (
    <Box sx={{ width: '100%' }}>
      <Tabs 
        value={tabValue} 
        onChange={handleTabChange}
        variant="fullWidth"
      >
        <Tab label="Summary" />
        <Tab label="Public Info" />
        <Tab label="Engagement" />
        <Tab label="Education" />
      </Tabs>

      <Box sx={{ p: 3 }}>
        {loading ? (
          <Box 
            display="flex" 
            justifyContent="center" 
            alignItems="center" 
            minHeight={200}
          >
            <CircularProgress />
          </Box>
        ) : (
          <Typography>
            {aiContent || 'Select a tab to generate AI-powered insights'}
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default AIAssistantTabs;
