import React, { useState, useEffect } from "react";
import {
  Container,
  Typography,
  Button,
  TextField,
  Card,
  CardContent,
  CardMedia,
  AppBar,
  Toolbar,
} from "@mui/material";
import "./App.css";
import { Grid } from "@mui/material/Grid"
import Autocomplete from "@mui/material/Autocomplete";
import axios from "axios";
import { MapContainer, TileLayer, GeoJSON } from "react-leaflet";
import L from 'leaflet';
import "leaflet/dist/leaflet.css";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

import countries from "./Countries.js";
// Fix Leaflet icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});


// Disaster Cards Component
const DisasterCards = () => {
  const disastersInfo = [
    { title: "Drought", description: "Prolonged period of insufficient rainfall." },
    { title: "Earthquake", description: "Sudden shaking of the ground." },
    { title: "Epidemic", description: "Widespread occurrence of an infectious disease." },
    { title: "Extreme Temperature", description: "Unusual and severe weather conditions." },
    { title: "Flood", description: "Overflow of water submerging land." },
    { title: "Landslide", description: "Earth sliding down a slope." },
    { title: "Storm", description: "Violent disturbance of the atmosphere." },
    { title: "Wildfire", description: "Uncontrolled fires in forests or grasslands." }
  ];

  return (
    <Grid container spacing={3}>
      {disastersInfo.map((disaster, index) => (
        <Grid item xs={12} sm={6} md={3} key={index}>
          <Card
            sx={{
              height: "100%",
              backgroundColor: "#edf2f4",
              display: "flex",
              flexDirection: "column",
              borderColor: "#2b2d42",
              borderRadius: 4,
            }}
          >
            <CardMedia
              component="img"
              image={`/disasterImages/${disaster.title.toLowerCase()}.png`}
              alt={disaster.title}
              sx={{ objectFit: "fit" }}
            />
            <CardContent sx={{ flexGrow: 1 }}>
              <Typography
                variant="h6"
                sx={{
                  fontFamily: "system-ui",
                  color: "#ef233c",
                  fontWeight: "bold",
                  textAlign: "center",
                }}
              >
                {disaster.title}
              </Typography>
              <Typography
                variant="body2"
                sx={{ color: "#2b2d42", textAlign: "center" }}
              >
                {disaster.description}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

// Stats Panel Component
const StatsPanel = ({ data }) => {
  if (!data || data.length === 0) {
    return (
      <Card sx={{ height: '100%', p: 2 }}>
        <Typography>No data available</Typography>
      </Card>
    );
  }

  const stats = data[0];

  return (
    <Card sx={{ height: '100%', p: 2 }}>
      <CardContent>
        <Typography variant="h6" sx={{ mb: 2 }}>Disaster Statistics</Typography>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <Typography variant="body1">
              Total Deaths: {stats.total_deaths?.toLocaleString() || 'N/A'}
            </Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="body1">
              Affected Population: {stats.affected_population?.toLocaleString() || 'N/A'}
            </Typography>
          </Grid>
          <Grid item xs={12}>
            <Typography variant="body1">
              Economic Loss: ${stats.economic_loss?.toLocaleString() || 'N/A'}
            </Typography>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

// Historical Trends Component
const HistoricalTrends = ({ data }) => {
  if (!data || data.length === 0) return null;

  // Process data for the chart
  const chartData = data.map(item => ({
    year: item.year,
    deaths: item.total_deaths,
    affected: item.affected_population,
  }));

  return (
    <Card sx={{ height: '100%', p: 2 }}>
      <CardContent>
        <Typography variant="h6" sx={{ mb: 2 }}>Historical Trends</Typography>
        <LineChart width={350} height={300} data={chartData} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="year" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="deaths" stroke="#ef233c" name="Deaths" />
          <Line type="monotone" dataKey="affected" stroke="#2b2d42" name="Affected" />
        </LineChart>
      </CardContent>
    </Card>
  );
};

// Map Component
const DisasterMap = ({ country, geoJsonData }) => {
  const [mapCenter, setMapCenter] = useState([0, 0]);
  const [zoom] = useState(4);

  useEffect(() => {
    if (geoJsonData) {
      try {
        const bounds = L.geoJSON(geoJsonData).getBounds();
        setMapCenter([
          (bounds.getNorth() + bounds.getSouth()) / 2,
          (bounds.getEast() + bounds.getWest()) / 2
        ]);
      } catch (error) {
        console.error('Error processing GeoJSON:', error);
        setMapCenter([0, 0]);
      }
    }
  }, [geoJsonData]);

  return (
    <Card sx={{ height: '100%', p: 2 }}>
      <CardContent>
        <Typography variant="h6" sx={{ mb: 2 }}>Impact Map</Typography>
        <div style={{ height: '400px', width: '100%' }}>
          <MapContainer
            center={mapCenter}
            zoom={zoom}
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; OpenStreetMap contributors'
            />
            {geoJsonData && (
              <GeoJSON
                data={geoJsonData}
                style={{
                  fillColor: '#ef233c',
                  weight: 2,
                  opacity: 1,
                  color: 'white',
                  fillOpacity: 0.7
                }}
              />
            )}
          </MapContainer>
        </div>
      </CardContent>
    </Card>
  );
};

// Main App Component
const App = () => {
  const [country, setCountry] = useState(null);
  const [year, setYear] = useState(null);
  const [disasterType, setDisasterType] = useState(null);
  const [availableYears, setAvailableYears] = useState([]);
  const [searchResults, setSearchResults] = useState(null);
  const [geoJsonData, setGeoJsonData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Sample data structure - replace with your actual data
  const disasters = [
    { label: "Drought" },
    { label: "Earthquake" },
    { label: "Epidemic" },
    { label: "Extreme Temperature" },
    { label: "Flood" },
    { label: "Landslide" },
    { label: "Storm" },
    { label: "Wildfire" },
  ];

  // Fetch available years when country and disaster type are selected
  useEffect(() => {
    const fetchAvailableYears = async () => {
      if (!country || !disasterType) {
        setAvailableYears([]);
        return;
      }

      try {
        const response = await axios.get('/api/available-years', {
          params: {
            country: country.code,
            disasterType: disasterType.label
          }
        });

        // Convert years to strings for Autocomplete
        setAvailableYears(response.data.map(String));

        // Reset year selection if previously selected year is not in new list
        if (year && !response.data.includes(parseInt(year))) {
          setYear(null);
        }
      } catch (error) {
        console.error('Error fetching available years:', error);
        setError('Failed to fetch available years');
        setAvailableYears([]);
      }
    };

    fetchAvailableYears();
  }, [country, disasterType]);

  const handleSearch = async () => {
    if (!country || !disasterType || !year) return;

    setLoading(true);
    setError(null);

    try {
      // Fetch disaster data
      const disasterResponse = await axios.get('/api/disasters', {
        params: {
          country: country.code,
          year,
          type: disasterType.label
        }
      });

      // Fetch GeoJSON data
      const geoJsonResponse = await axios.get(`/api/geojson/${country.code.toLowerCase()}`);

      setSearchResults(disasterResponse.data);
      setGeoJsonData(geoJsonResponse.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to fetch disaster data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="xl">
           <AppBar position="static" sx={{ backgroundColor: "#2b2d42", mb: 4 }}>
        <Toolbar>
          <Typography
            variant="h2"
            sx={{
              flexGrow: 1,
              textAlign: 'center',
              fontFamily: "DisplayFont",
              color: "#edf2f4",
            }}
          >
            DoomIndex
          </Typography>
        </Toolbar>
      </AppBar>

      <Typography variant="h4" sx={{ mb: 2 }}>
        Welcome to DoomIndex
      </Typography>

      <Typography variant="body1" sx={{ mb: 4 }}>
      DoomIndex is your comprehensive platform for accessing and understanding disaster-related information from across the globe. This interactive tool merges historical disaster data with advanced AI technology to offer valuable insights and enhance disaster response and preparedness.
      </Typography>

      <Typography variant="h4" sx={{ mb: 2 }}>
      Key Features
      </Typography>

      <Typography variant="body1" sx={{ mb: 4 }}>
        <ul>
      <li> Advanced disaster search and filtering capabilities </li>
      <li> AI-powered disaster summaries and analysis</li>
      <li> Real-time public information updates</li>
      <li> Interactive disaster response chatbot</li>
      <li> Educational resources and preparedness guides</li>
      </ul>
      </Typography>

      <Typography variant="h4" sx={{ mb: 2 }}>
      What You Can Do
      </Typography>

      <Typography variant="body1" sx={{ mb: 4 }}>
      <ul>
    <li>Search and explore historical disaster data</li>
    <li>Gain detailed insights about specific disasters</li>
    <li>Access preparedness and response plans</li>
    <li>Engage with our AI-powered chatbot for disaster-related queries</li>
    <li>Learn about disaster prevention and preparedness</li>
  </ul>
      </Typography>

      <Typography variant="h4" sx={{ mb: 2 }}>
      How It Works
      </Typography>

      <Typography variant="body1" sx={{ mb: 4 }}>
      The DoomIndex dashboard utilizes cutting-edge AI technology powered by Google's Gemini to analyze disaster data and deliver meaningful insights. Simply search for a disaster by country, year, or type, and instantly access comprehensive information, including summaries, public updates, response plans, and educational content. Our interactive chatbot is available to answer your specific questions about any disaster in our database.
      </Typography>
      <Typography variant="body1" sx={{ mb: 4 }}>
      Data is sourced from verified global disaster databases and processed in real-time to ensure the most up-to-date and relevant information. In case of an emergency, please always contact your local emergency services first.
      </Typography>

      <DisasterCards />

      <Grid container spacing={2} sx={{ mt: 4, mb: 4 }}>
        <Grid item xs={12} sm={3}>
          <Autocomplete
            options={countries}
            value={country}
            onChange={(_, newValue) => {
              setCountry(newValue);
              setYear(null); // Reset year when country changes
            }}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Country"
                variant="outlined"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    borderRadius: '30px',
                    backgroundColor: '#edf2f4',
                  }
                }}
              />
            )}
          />
        </Grid>

        <Grid item xs={12} sm={3}>
          <Autocomplete
            options={disasters}
            value={disasterType}
            onChange={(_, newValue) => {
              setDisasterType(newValue);
              setYear(null); // Reset year when disaster type changes
            }}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Disaster Type"
                variant="outlined"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    borderRadius: '30px',
                    backgroundColor: '#edf2f4',
                  }
                }}
              />
            )}
          />
        </Grid>

        <Grid item xs={12} sm={3}>
          <Autocomplete
            options={availableYears}
            value={year}
            onChange={(_, newValue) => setYear(newValue)}
            disabled={!country || !disasterType}
            renderInput={(params) => (
              <TextField
                {...params}
                label="Year"
                variant="outlined"
                sx={{
                  '& .MuiOutlinedInput-root': {
                    borderRadius: '30px',
                    backgroundColor: '#edf2f4',
                  }
                }}
                helperText={
                  !country || !disasterType 
                    ? "Select country and disaster type first" 
                    : availableYears.length === 0 
                    ? "No years available" 
                    : ""
                }
              />
            )}
          />
        </Grid>

        <Grid item xs={12} sm={3}>
          <Button
            variant="contained"
            onClick={handleSearch}
            disabled={!country || !disasterType || !year || loading}
            sx={{
              borderRadius: '30px',
              backgroundColor: '#ef233c',
              color: '#edf2f4',
              padding: '15px 30px',
              '&:hover': {
                backgroundColor: '#d90429',
              },
              '&:disabled': {
                backgroundColor: '#8d99ae',
              },
            }}
          >
            {loading ? 'Searching...' : 'Search'}
          </Button>
        </Grid>
      </Grid>


      {error && (
        <Typography color="error" sx={{ mt: 2, mb: 2 }}>
          {error}
        </Typography>
      )}

      {searchResults && (
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <DisasterMap country={country} geoJsonData={geoJsonData} />
          </Grid>
          <Grid item xs={12} md={4}>
            <StatsPanel data={searchResults} />
          </Grid>
          <Grid item xs={12} md={4}>
            <HistoricalTrends data={searchResults} />
          </Grid>
        </Grid>
      )}

      <footer style={{
        backgroundColor: "#2b2d42",
        color: "#edf2f4",
        padding: "20px",
        marginTop: "40px",
        textAlign: "center"
      }}>
        <Typography variant="body2">
          Contact: info@doomindex.com | Â© 2024 DoomIndex. All Rights Reserved.
        </Typography>
      </footer>
    </Container>
  );
};

export default App;
